import React from "react";
import { Link } from "react-router-dom";

function Menu() {

    function handleHover() {

        const menu = document.getElementsByClassName("homeMenu");

        Array.from(document.getElementsByClassName("homeMenu-item"))
            .forEach((item, index) => {
                item.onmouseover = () => {
                    menu[0].setAttribute("index", index);
                    console.log(index);
                }
            })
    }

    function handleLeave() {
        Array.from(document.getElementsByClassName("homeMenu"))
            .forEach(item => {
                item.removeAttribute("index");
            })
    }

    return (
        <div className="menu">
            <ul className="homeMenu">
                <li onMouseEnter={handleHover} onMouseLeave={handleLeave}><Link className="homeMenu-item" to={"about/"}>About Me</Link></li>
                <li onMouseEnter={handleHover} onMouseLeave={handleLeave}><Link className="homeMenu-item" to={"about/"}>Skills</Link></li>
                <li onMouseEnter={handleHover} onMouseLeave={handleLeave}><Link className="homeMenu-item" to={"about/"}>Virtual Assistant</Link></li>
            </ul>
            <div className="pattern"></div>
            <div className="pattern-image"></div>
        </div>
    );
}

export default Menu;
