import React from "react";

function Menu() {

    function handleHover() {
        const menu = document.querySelector("homeMenu");

        console.log(menu);

        Array.from(document.getElementsByClassName("homeMenu-item"))
            .forEach((item, index) => {
                item.onmouseover = () => {
                    console.log(index);
                    menu[0].setAttribute("index", index);
                }
            })
    }


    return (
        <div className="menu">
            <ul className="homeMenu">
                <li className="homeMenu-item" onMouseEnter={handleHover}>About Me</li>
                <li className="homeMenu-item" onMouseEnter={handleHover}>Skills</li>
                <li className="homeMenu-item" onMouseEnter={handleHover}>Virtual Assistant</li>
            </ul>
            <div className="pattern"></div>
        </div>
    );
}

export default Menu;
