import React from "react";

function Menu() {

    function handleHover() {

        const menu = document.getElementsByClassName("homeMenu");

        Array.from(document.getElementsByClassName("homeMenu-item"))
            .forEach((item, index) => {
                item.onmouseover = () => {
                    menu[0].setAttribute("index", index);
                }
            })
    }

    function handleLeave() {
        Array.from(document.getElementsByClassName("homeMenu"))
            .forEach((item, index) => {
                item.removeAttribute("index");
            })
    }

    return (
        <div className="menu">
            <ul className="homeMenu">
                <li className="homeMenu-item" onMouseEnter={handleHover} onMouseLeave={handleLeave}>About Me</li>
                <li className="homeMenu-item" onMouseEnter={handleHover} onMouseLeave={handleLeave}>Skills</li>
                <li className="homeMenu-item" onMouseEnter={handleHover} onMouseLeave={handleLeave}>Virtual Assistant</li>
            </ul>
            <div className="pattern"></div>
            <div className="pattern-image"></div>
        </div>
    );
}

export default Menu;
