import React from "react";

function Menu() {

    const menu = document.getElementsByClassName("homeMenu");

    console.log(menu[0]);

    Array.from(document.getElementsByClassName("homeMenu-item"))
        .forEach((item, index) => {
            item.onmouseover = () => {
                console.log(index);
                menu[0].setAttribute("index", index);
            }
        })



    return (
        <div className="menu">
            <ul className="homeMenu">
                <li className="homeMenu-item">About Me</li>
                <li className="homeMenu-item">Skills</li>
                <li className="homeMenu-item">Virtual Assistant</li>
            </ul>
            <div className="pattern"></div>
        </div>
    );
}

export default Menu;
