import React, { useEffect } from "react";
import Contacts from "./Contacts";
import BackButton from "./BackButton";

function About() {

    useEffect(() => {
        const observer = new IntersectionObserver(element => {
            element.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add("show");
                } else {
                    entry.target.classList.remove("show")
                }
            })
        })

        const hiddenElements = document.querySelectorAll(".hidden");
        hiddenElements.forEach(element => observer.observe(element));
    });

    return (
        <div>
            <BackButton />
            <div className="topSection">
                <h1 className="header">Who am I?</h1>
                {/* <img className="circuit-image1" src="../../images/circuit1.svg" alt="circuit1" /> */}
            </div>

            <div className="middleSection hidden hidden-left">
                <div className="wrapper">
                    <div className="avatar-border">
                        <img className="profile" src="../../images/Bruno.png" alt="" />
                    </div>
                </div>
                <p className="text text-mid">
                    I'm an Electrical and Computer engineer who is
                    passionate about technology, and other problem solving
                    fields. Additionally, I love facing new challenges,
                    learning about different cultures and sharing
                    experiences with others ..
                </p>
            </div>
            {/* <img className="circuit-image2" src="../../images/circuit2.svg" alt="circuit2" /> */}


            <div className="bottomSection hidden hidden-right">
                <p className="text text-bot">
                    It is a long established fact that a reader will be
                    distracted by the readable content of a page when looking
                    at its layout. The point of using Lorem Ipsum is that it
                    has a more-or-less normal distribution of letters, as
                    opposed to using 'Content here, content here',
                    making it look like readable English. Many desktop
                    publishing packages and web page editors now use Lorem Ipsum
                    as their default model text, and a search for 'lorem ipsum'
                    will uncover many web sites still in their infancy. Various
                    versions have evolved over the years, sometimes by accident,
                    sometimes on purpose (injected humour and the like).
                </p>
                <div className="wrapper">
                    <div className="carousel">
                        <div className="element"></div>
                    </div>
                </div>
                {/* <img className="circuit-image3" src="../../images/circuit3.svg" alt="circuit3" /> */}
            </div>
            <Contacts />
        </div>
    );

}

export default About;
