import React from "react";


function Menu() {

    let menu = document.getElementsByClassName("homeMenu");

    Array.from(document.getElementsByClassName("homeMenu-item"))
        .forEach((item, index) => {
            item.onmouseover = () => {
                menu.dataset.activeIndex = index;
            }
        })

    return (
        <div className="menu">
            <ul className="homeMenu">
                <li className="homeMenu-item">About Me</li>
                <li className="homeMenu-item">Skills</li>
                <li className="homeMenu-item">Virtual Assistant</li>
            </ul>
            <div className="pattern"></div>
        </div>
    );
}

export default Menu;
